import React from 'react'
import {useEffect, useState} from "react"
import  Axios  from 'axios'
import Coin from './Coin'
import "./App.css"
import { FilledInput, Input, InputLabel, OutlinedInput } from '@material-ui/core'


function App() {
    const[allcoins,setallcoins]=useState([])
    const [searchword,setsearch]=useState("");
    useEffect(()=>{
       Axios.get("https://api.coinstats.app/public/v1/coins?skip=0").then((resp)=>{
          setallcoins(resp.data.coins)
       });
    },[])
    const filterCoins= allcoins.filter((coin)=>{
        return coin.name.toLowerCase().includes(searchword.toLowerCase());
    })
    
    return (
        <>
            <div class="container">
      <div class="d-flex flex-wrap align-items-center justify-content-center justify-content-lg-start">
        <a href="/" class="d-flex align-items-center mb-2 mb-lg-0 text-white text-decoration-none">
          <svg class="bi me-2" width="40" height="32" role="img" aria-label="Bootstrap"></svg>
        </a>

        <ul class="nav col-12 col-lg-auto me-lg-auto mb-2 justify-content-center mb-md-0">
          <li><a href="#" class="nav-link px-2 text-secondary">Home</a></li>
          <li><a href="#" class="nav-link px-2 text-white">Features</a></li>
          <li><a href="#" class="nav-link px-2 text-white">Pricing</a></li>
          <li><a href="#" class="nav-link px-2 text-white">FAQs</a></li>
          <li><a href="#" class="nav-link px-2 text-white">About</a></li>
        </ul>

        <form class="col-12 col-lg-auto mb-3 mb-lg-0 me-lg-3" role="search">
          <input type="search" class="form-control form-control-dark text-white bg-dark" placeholder="Search..." aria-label="Search"/>
        </form>

        <div class="text-end">
          <button type="button" class="btn btn-outline-light me-2">Login</button>
          <button type="button" class="btn btn-warning">Sign-up</button>
        </div>
      </div>
    </div>
            <div className="container-flush text-center">
              <div className='cryptoh'>
               <Input placeholder='Search any coin.....' onChange={(e)=>{setsearch(e.target.value)}}/>
              </div>
              <div className='cryptod'>{filterCoins.map((coin)=>{
                  return <Coin name={coin.name} icon={coin.icon} price={coin.price} symbol={coin.symbol}/>
              })}</div>
              
            </div>
        </>
    )
}

export default App
