import React from 'react'
import { useEffect, useState } from "react"
import Axios from 'axios'
import Coin from './Coin'
import "./App.css"
import { FilledInput, Input, InputLabel, OutlinedInput } from '@material-ui/core'


function App() {
    const [allcoins, setallcoins] = useState([])
    const [searchword, setsearch] = useState("");
    useEffect(() => {
        Axios.get("https://api.coinstats.app/public/v1/coins?skip=0").then((resp) => {
            setallcoins(resp.data.coins)
        });
    }, [])
    const filterCoins = allcoins.filter((coin) => {
        return coin.name.toLowerCase().includes(searchword.toLowerCase());
    })

    return (
        <>
            <div class="d-flex h-100 text-center text-white bg-dark">

                <div class="cover-container d-flex w-100 h-100 p-3 mx-auto flex-column">
                    <header class="mb-auto">
                        <div>
                            <h3 class="float-md-start mb-0"><a href="/"><i class="bi bi-arrow-left-circle-fill"></i></a>&nbsp;&nbsp;Create an Invoice</h3>
                             <nav class="nav nav-masthead justify-content-center float-md-end">
                <a class="nav-link active text-light" aria-current="page" href="/">Home</a>
                <a class="nav-link text-light active" href="#">Features</a>
                <a class="nav-link text-light active" href="#">Contact</a>
            </nav>
                        </div>
                    </header>

                    <main class="px-3">
                        <h1>Track price of any cryptocurrency</h1>
                        <p class="lead">Get real time price and details of any cryptocurrency in the world.</p>
                        
                    </main>

               
                </div>





            </div>
            <div className="container-flush text-center">
                <div className='cryptoh bg-dark'>
                    <Input id="inp" placeholder='Search any coin.....' onChange={(e) => { setsearch(e.target.value) }} />
                </div>
                <div className='cryptod'>{filterCoins.map((coin) => {
                    return <Coin name={coin.name} icon={coin.icon} price={coin.price} symbol={coin.symbol} mktcap={coin.marketCap}
                    pc1h={coin.priceChange1h} pc2h={coin.priceChange1d}  pc3h={coin.priceChange1w} rank={coin.rank}/>
                })}</div>

            </div>
        </>
    )
}

export default App
